import React, { useEffect, useRef } from "react";

const SuccessStories = () => {
  const stories = [
    {
      id: 1,
      img: "data:image/jpeg;base64,",
      name: "John Doe",
      story:
        "Participating in this marathon changed my life. I lost 20 pounds and made amazing friends!",
    },
    {
      id: 2,
      img: "data:image/jpeg;base64,",
      name: "Jane Smith",
      story:
        "Winning this race gave me the confidence to achieve more. Grateful for the experience!",
    },
    {
      id: 3,
      img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUxzbdnJY6_yBsq76Akfz77o0u6q2-m-B4Dw&s",
      name: "Sam Wilson",
      story:
        "Running for a cause was inspiring. This event will always have a special place in my heart.",
    },
    {
      id: 4,
      img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSr9lYi2bVOda2Mw4AaX6yVvUijWc-3APJ4OA&s",
      name: "Emily Clark",
      story:
        "The marathon pushed me to new limits, and I can't wait to do it again next year!",
    },
    {
      id: 5,
      img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcROGDz-w47hC_QSkyXsyv-DS3A-DYyPrXILpg&s",
      name: "Michael Brown",
      story:
        "Crossing the finish line was one of the most rewarding experiences of my life.",
    },
  ];

  const sliderRef = useRef(null);

  // Auto sliding function
  useEffect(() => {
    const slider = sliderRef.current;
    let interval = setInterval(() => {
      if (slider) {
        slider.scrollLeft += slider.offsetWidth;
        if (
          slider.scrollLeft + slider.offsetWidth >=
          slider.scrollWidth
        ) {
          slider.scrollLeft = 0;
        }
      }
    }, 3000); // Change slide every 3 seconds

    return () => clearInterval(interval); // Cleanup
  }, []);

  return (
    <section className="py-12 px-4 bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 text-white">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-4xl font-bold text-center mb-8">
          Success Stories
        </h2>
        <div
          ref={sliderRef}
          className="flex overflow-x-auto scrollbar-hide snap-x snap-mandatory"
        >
          {stories.map((story) => (
            <div
              key={story.id}
              className="flex-shrink-0 w-72 sm:w-80 lg:w-96 p-4 snap-start"
            >
              <div className="card bg-white shadow-lg rounded-lg overflow-hidden text-gray-800">
                <img
                  src={story.img}
                  alt={story.name}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-xl font-semibold mb-2">{story.name}</h3>
                  <p className="text-sm">{story.story}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="flex justify-center mt-8 space-x-2">
          {stories.map((_, index) => (
            <span
              key={index}
              className="inline-block w-3 h-3 bg-white rounded-full opacity-50"
            ></span>
          ))}
        </div>
      </div>
    </section>
  );
};

export default SuccessStories;
